INSERT INTO `reviews` (`id`, `user_id`, `theme`, `message`, `date`) VALUES
(1, 2, 'Первый отзыв', 'Мне понравился этот сайт! Здесь очень красивый дизайн и интуитивно понятный интерфейс. Но хотелось бы видедь больше информации о проходящих матчах, например, составы на матч и авторов голов. Надеюсь, в будущем это добавят.', '2022-04-20 16:17:59'),
(2, 7, 'Моё мнение', 'Добрый день, решил оставить здесь свой отзыв. Считаю, что этот сайт лучше, чем МатчТВ или Sportbox. Спасибо!)', '2022-04-22 12:19:51'),
(3, 11, 'Малый функционал сайта', 'Такое ощущение, что данный сайт сваял за день какой-то новичок!', '2022-04-23 22:31:08'),
(4, 12, 'Не придирайтесь', 'Да ну, вроде ниче такой сайтец, буду сюда периодически заходить. Новости читать здесь очень удобно', '2022-04-23 23:32:56'),
(5, 10, 'Привет, админ. Это я, твой единственный зритель.', 'Привет, админ. Это я, твой единственный зритель. Я на протяжении многих лет создавал иллюзию того, что тебя смотрит много людей, но это был я. Сейчас напишу это сообщение со всех аккаунтов.', '2022-04-25 16:00:43'),
(6, 8, 'Привет, админ. Это я, твой единственный зритель.', 'Привет, админ. Это я, твой единственный зритель. Я на протяжении многих лет создавал иллюзию того, что тебя смотрит много людей, но это был я. Сейчас напишу это сообщение со всех аккаунтов.', '2022-04-27 12:55:16'),
(7, 1, 'Админ в здании', 'Так, а ну-ка не спамить!', '2022-04-28 22:06:26'),
(8, 14, 'root', 'root', '2022-05-06 00:00:13');

